import React, { useState, useEffect } from 'react';
import { Card, Table } from 'react-bootstrap';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts'; // 保留这些导入，后续可能会使用
import * as XLSX from 'xlsx';
import './App.css';

import logo from '../assets/logo.jpg'; // 导入logo图片

// 考勤数据模型
class Attendance {
  constructor(idnum, department, expected, actual, attendance_rate, absentees, remarks) {
    this.idnum = idnum;
    this.department = department;
    this.expected = expected;
    this.actual = actual;
    this.attendance_rate = attendance_rate;
    this.absentees = absentees;
    this.remarks = remarks;
  }
}

function App() {
  // 状态管理
  const [state, setState] = useState({
    data: [],
    center_data: [],
    current_time: '',
    day_in: 0,
    day_out: 0,
    sum_number: 0,
    chart_data: [],
    male_number: 1000,
    female_number: 1500
  });

  // 定时更新数据
  useEffect(() => {
    const interval = setInterval(() => {
      updateData();
      updateTime();
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // 更新时间
  const updateTime = () => {
    const currentTime = new Date().toLocaleDateString('zh-CN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    });
    setState(prev => ({...prev, current_time: `${currentTime} 部门及车间考勤数据看板`}));
  };

  // 更新数据
  const updateData = async () => {
    try {
      const response = await fetch('attendance.xlsx');
      const arrayBuffer = await response.arrayBuffer();
      const workbook = XLSX.read(arrayBuffer);
      
      // 获取工作表数据
      const worksheet = workbook.Sheets[workbook.SheetNames[0]];
      const hrWorksheet = workbook.Sheets[workbook.SheetNames[0]];
      
      // 转换为JSON格式
      const data = XLSX.utils.sheet_to_json(worksheet, {skipRows: [0,40,41,42]});
      const hrData = XLSX.utils.sheet_to_json(hrWorksheet);
      
      // 提取HR数据
      const hrRow = hrData[40];
      const sumNumber = hrRow ? parseInt(hrRow['__EMPTY_2']) : 0;
      const dayIn = hrRow ? parseInt(hrRow['__EMPTY']) : 0;
      const dayOut = hrRow ? parseInt(hrRow['__EMPTY_1']) : 0;
      
      // 处理部门数据
      const chartData = data.map(row => ({
        name: row['部门'],
        value: parseInt(row['应到人数'])
      }));
      
      // 筛选车间和部门数据
      const isWorkshop = (dept) => dept && dept.includes('车间');
      const workshopData = data
        .filter(row => isWorkshop(row['部门']))
        .sort((a, b) => b['出勤率'] - a['出勤率'])
        .map((row, i) => ({
          id: i,
          department: row['部门'],
          expected: parseInt(row['应到人数']),
          actual: parseInt(row['实到人数']),
          attendance_rate: Math.round(row['出勤率'] * 100),
          absentees: row['缺勤人员姓名及原因'] || '',
          remarks: row['备注'] || ''
        }));
      
      const deptData = data
        .filter(row => !isWorkshop(row['部门']))
        .sort((a, b) => b['出勤率'] - a['出勤率'])
        .map((row, i) => ({
          id: i,
          department: row['部门'],
          expected: parseInt(row['应到人数']),
          actual: parseInt(row['实到人数']),
          attendance_rate: Math.round(row['出勤率'] * 100),
          absentees: row['缺勤人员姓名及原因'] || '',
          remarks: row['备注'] || ''
        }));
      
      // 更新状态
      setState(prev => ({
        ...prev,
        data: deptData,
        center_data: workshopData,
        chart_data: chartData,
        sum_number: sumNumber,
        day_in: dayIn,
        day_out: dayOut
      }));
      
    } catch (error) {
      console.error('加载数据失败:', error);
    }
  };

  const generateColors = (data) => {
    return data.map(row => {
      if (row.value < 70) return '#ffa09e';
      if (row.value < 90) return '#ffdca2';
      return '#a4ccff';
    });
  };


  return (
    <div className="app-container">
      {/* 顶部标题和时间 */}
      <div className="header">
        <img src={logo} alt="Logo" className="logo" />
        <h1 className="title">{state.current_time}</h1>
      </div>

      {/* 统计卡片 */}
      <div className="stats-container">
        {/* 总人数卡片 */}
        <Card style={{backgroundColor: '#265589', color: '#fdf5f3', boxShadow: '0 4px 6px -11px rgba(10, 9, 60, 0.1)', width: '150px', padding: '0.5rem', borderRadius: '1rem', textAlign: 'center', margin: '0.5rem'}}>
          <Card.Body>
            <img src="public/assets/kehu.png" style={{width: '50px', height: 'auto'}} alt="总人数" />
            <h4>总人数</h4>
            <h2>{state.sum_number}</h2>
          </Card.Body>
        </Card>

        {/* 本日入职人数卡片 */}
        <Card style={{backgroundColor: '#8ea9c4', color: '#fdf5f3', boxShadow: '0 4px 6px -11px rgba(15, 83, 42, 0.1)', width: '150px', padding: '0.5rem', borderRadius: '1rem', textAlign: 'center', margin: '0.5rem'}}>
          <Card.Body>
            <img src="public/assets/zhuanshenren.png" style={{width: '50px', height: 'auto'}} alt="本日入职" />
            <h4>本日入职人数</h4>
            <h2>{state.day_in}</h2>
          </Card.Body>
        </Card>

        {/* 本月离职人数卡片 */}
        <Card style={{backgroundColor: '#cbaea8', color: '#fdf5f3', boxShadow: '0 4px 6px -11px rgba(67, 11, 53, 0.1)', width: '150px', padding: '0.5rem', borderRadius: '1rem', textAlign: 'center', margin: '0.5rem'}}>
          <Card.Body>
            <img src="assets/zhuanshenren.png" style={{width: '50px', height: 'auto'}} alt="本月离职" />
            <h4>本月离职人数</h4>
            <h2>{state.day_out}</h2>
          </Card.Body>
        </Card>
      </div>

      {/* 图表和表格部分 */}
      <div className="content-container">
        {/* 图表组件 */}
        <div style={{padding: '1rem', margin: 'auto'}}>
          <h3>各部门人数分布</h3>
          <div style={{overflowX: 'auto', whiteSpace: 'nowrap', padding: '1rem', margin: 'auto'}}>
            <ResponsiveContainer width="100%" height={400}>
              <BarChart data={state.chart_data} margin={{top: 20, right: 30, left: 20, bottom: 5}}>
                <XAxis dataKey="name" angle={-45} textAnchor="end" height={60} />
                <YAxis />
                <Tooltip />
                <Bar dataKey="value" fill="#8884d8" />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* 表格组件 */}
        <div style={{display: 'flex', justifyContent: 'space-between'}}>
          {/* 车间表格 */}
          <div style={{width: '48%', display: 'inline-block', verticalAlign: 'top'}}>
            <h3>车间每日出勤</h3>
            <Table striped bordered hover responsive style={{width: '100%'}}>
              <thead>
                <tr>
                  <th style={{minWidth: '100px', textAlign: 'center', backgroundColor: '#D8D7D7'}}>车间</th>
                  <th style={{textAlign: 'center', backgroundColor: '#D8D7D7'}}>应到人数</th>
                  <th style={{textAlign: 'center', backgroundColor: '#D8D7D7'}}>实到人数</th>
                  <th style={{textAlign: 'center', backgroundColor: '#D8D7D7'}}>出勤率</th>
                </tr>
              </thead>
              <tbody>
                {state.center_data.map((row, i) => (
                  <tr key={i} style={{backgroundColor: i % 2 === 0 ? '#E7E7E7' : 'white'}}>
                    <td style={{fontWeight: 'bold', color: '#2d3748'}}>{row.department}</td>
                    <td style={{textAlign: 'center'}}>{row.expected}</td>
                    <td style={{textAlign: 'center'}}>{row.actual}</td>
                    <td>
                      <div style={{
                        backgroundColor: row.attendance_rate >= 100 ? '#4cb272' : row.attendance_rate >= 70 ? '#f6e05e' : 'red',
                        color: row.attendance_rate >= 100 ? 'white' : '#2d3748',
                        padding: '0.1em',
                        borderRadius: '4px',
                        textAlign: 'center'
                      }}>
                        {row.attendance_rate}%
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>

          {/* 部门表格 */}
          <div style={{width: '48%', display: 'inline-block', verticalAlign: 'top'}}>
            <h3>部门每日出勤</h3>
            <Table striped bordered hover responsive style={{width: '100%'}}>
              <thead>
                <tr>
                  <th style={{minWidth: '100px', textAlign: 'center', backgroundColor: '#D8D7D7'}}>部门</th>
                  <th style={{textAlign: 'center', backgroundColor: '#D8D7D7'}}>应到人数</th>
                  <th style={{textAlign: 'center', backgroundColor: '#D8D7D7'}}>实到人数</th>
                  <th style={{textAlign: 'center', backgroundColor: '#D8D7D7'}}>出勤率</th>
                </tr>
              </thead>
              <tbody>
                {state.data.map((row, i) => (
                  <tr key={i} style={{backgroundColor: i % 2 === 0 ? '#E7E7E7' : 'white'}}>
                    <td style={{fontWeight: 'bold', color: '#2d3748'}}>{row.department}</td>
                    <td style={{textAlign: 'center'}}>{row.expected}</td>
                    <td style={{textAlign: 'center'}}>{row.actual}</td>
                    <td>
                      <div style={{
                        backgroundColor: row.attendance_rate >= 100 ? '#4cb272' : row.attendance_rate >= 70 ? '#f6e05e' : 'red',
                        color: row.attendance_rate >= 100 ? 'white' : '#2d3748',
                        padding: '0.1em',
                        borderRadius: '4px',
                        textAlign: 'center'
                      }}>
                        {row.attendance_rate}%
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
